<?php

namespace BlackSheep\MusicLibraryBundle\Repository;

use BlackSheep\MusicLibraryBundle\Entity\SongEntity;

/**
 * SongsRepository.
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SongsRepository extends AbstractRepository implements SongsRepositoryInterface
{
    /**
     * @param $songInfo
     *
     * @return null|SongEntity
     */
    public function needsImporting($songInfo)
    {
        return $this->findOneBy(['mTime' => $songInfo['mTime'], 'path' => $songInfo['path']]);
    }

    /**
     * @return null|\DateTime
     */
    public function lastImportDate()
    {
        $lastCall = $this->findOneBy([], ['mTime' => 'DESC']);
        $lastImportDate = null;
        if ($lastCall !== null) {
            $lastImportDate = new \DateTime();
            $lastImportDate->setTimestamp($lastCall->getMTime());
            unset($lastCall);
        }

        return $lastImportDate;
    }
}
